export default async function(wishList = [], action){

    if(action.type == 'addArticle'){
        var wishListCopy = [...wishList]

        var findArticle = false

        for(let i=0;i<wishListCopy.length;i++){
            if(wishListCopy[i].title == action.articleLiked.title){
                findArticle = true
            }
        }

        if(!findArticle){
            wishListCopy.push(action.articleLiked)
        }
        const data = await fetch(`/add-wishlist`, {
            method: 'POST',
            headers: {'Content-Type': 'application/x-www-form-urlencoded'},
            body: `title=${article.title}&description=${article.description}&content=${article.content}&image=${article.urlToImage}`
          })
          const body = await data.json()
          console.log(body)

        return wishListCopy
    } else if(action.type == 'deleteArticle'){
        var wishListCopy = [...wishList]
        var position = null

        for(let i=0;i<wishListCopy.length;i++){
            if(wishListCopy[i].title == action.title){
                position = i
            }
        }
        if(position != null){
            wishListCopy.splice(position,1)
        }

        return wishListCopy

    } else {
        return wishList
    }
}
